import React from 'react';
import { useEffect, useState } from "react";
const MenuData = [
  {
    id: 1,
    image: "../images/chola.jpg",
    name: "chola",
    category: "lunch",
    price: "5£",
    description:
      "Lorem ipsum dolor sit amet consectetur adipisicing elit. Blanditiis, at consectetur totam voluptatibus quibusdam iusto. Accusamus quas, soluta ipsam autem eius necessitatibus fugiat in . ",
  }
];
const Navbar = () => {
  const [activeCategory, setActiveCategory] = useState("all");
  const [filteredItems, setFilteredItems] = useState([]);

  useEffect(() => {
    const btnGroupItems = document.querySelectorAll(".btn-group__item");
    btnGroupItems.forEach((item) => {
      item.addEventListener("click", (e) => {
        const category = e.target.dataset.category;
        setActiveCategory(category);
      });
    });
  }, []);

  useEffect(() => {
    if (activeCategory === "all") {
      setFilteredItems(MenuData);
    } else {
      const filtered = MenuData.filter((item) => item.category === activeCategory);
      setFilteredItems(filtered);
    }
  }, [activeCategory]);

  return (
    <div className="navbar">
      <nav className="btn-group">
        <div className="logo">
          <img src="/images/logo.png" alt=" Logo" />
        </div>
        <span className="btn-group__item" data-category="breakfast">
          Breakfast
        </span>
        <span className="btn-group__item" data-category="evening">
          Evening
        </span>
        <span className="btn-group__item" data-category="lunch">
          Lunch
        </span>
        <span className="btn-group__item" data-category="dinner">
          Dinner
        </span>
        <span className="btn-group__item" data-category="all">
          All
        </span>
        <h1 className="btn-group__item">Discover good taste with us</h1>
      </nav>
      <ul>
        {filteredItems.map((MenuData) => (
          <li key={MenuData.name}>{MenuData.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default Navbar;



