import React, { useState } from 'react';
import MenuData from './menuApi';

const Navbar = () => {
  const [currentCategory, setCurrentCategory] = useState('all');

  const handleClick = (category) => {
    setCurrentCategory(category);
  };

  const filteredData = MenuData.filter((item) => {
    if (currentCategory === 'all') {
      return true;
    } else {
      return item.category === currentCategory;
    }
  });

  return (
    <div className="navbar">
      <nav className="btn-group">
        <div className="logo">
          <img src='/images/logo.png' alt=" Logo" />
        </div>
        <span
          className="btn-group__item"
          onClick={() => handleClick('breakfast')}
        >
          Breakfast
        </span>
        <span
          className="btn-group__item"
          onClick={() => handleClick('evening')}
        >
          Evening
        </span>
        <span
          className="btn-group__item"
          onClick={() => handleClick('lunch')}
        >
          Lunch
        </span>
        <span
          className="btn-group__item"
          onClick={() => handleClick('dinner')}
        >
          Dinner
        </span>
        <span
          className="btn-group__item"
          onClick={() => handleClick('all')}
        >
          All
        </span>
        <span href='AboutMe.jsx' className="btn-group__item">About us</span>
        <h1 className="btn-group__item">Discover good taste with us</h1>
      </nav>
      <section className="main-card--cointainer"> 
      {filteredData.map((menu) => (
        <div key={menu.id} className="card-container">
          <div className="card">
            <div className="card-body">
              <span className="card-number card-circle subtle">{menu.id}</span>
              <span className="card-author subtle">{menu.category}</span>
              <h2 className="card-title">{menu.name}</h2>
              <span className="card-description subtle">{menu.description}</span>
              <div className="card-read">{menu.price}</div>
            </div>
            <img src={menu.image} alt={menu.name} className="card-media" />
            <span className="card-tag  subtle">Order Now</span>
          </div>
        </div>
      ))}
       </section>
    </div>
  );
};

export default Navbar;
