import React, { useState } from 'react';
import MenuData from './Menu';

const Menu = () => {
  const [category, setCategory] = useState('all');
  const menuItems = MenuData.filter((item) => {
    if (category === 'all') {
      return true;
    } else {
      return item.category === category;
    }
  });

  const handleCategoryClick = (event) => {
    const newCategory = event.target.dataset.category;
    setCategory(newCategory);
  };

  return (
    <div className="menu">
      <div className="menu-buttons">
        <button onClick={handleCategoryClick} data-category="all">
          All
        </button>
        <button onClick={handleCategoryClick} data-category="breakfast">
          Breakfast
        </button>
        <button onClick={handleCategoryClick} data-category="lunch">
          Lunch
        </button>
        <button onClick={handleCategoryClick} data-category="evening">
          Evening
        </button>
        <button onClick={handleCategoryClick} data-category="dinner">
          Dinner
        </button>
      </div>
      <div className="menu-items">
        {menuItems.map((item) => (
          <div key={item.id} className="menu-item">
            <img src={item.image} alt={item.name} />
            <div className="item-info">
              <h3>{item.name}</h3>
              <h4>{item.price}</h4>
              <p>{item.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Menu;
